 export const EXPERIENCIA_W = [
    {
        title: "",
        date: "",
        responsabilidades: [],
        imagenes: []
    }
];

async function fetchData() {
    try {
        const response = await fetch('http://127.0.0.1:8000/movies'); // Reemplaza con tu endpoint de la API
        const data = await response.json();

        console.log('Datos recibidos:', data); // Verifica los datos recibidos

        // Limpiamos el arreglo para evitar datos repetidos en múltiples llamadas
        EXPERIENCIA_W.length = 0;

        data.forEach(item => {
            EXPERIENCIA_W.push({
                title: item.title,
                date: item.release_date,
                responsabilidades: [item.description],
                imagenes: [item.image_url]
            });
        });

        console.log('EXPERIENCIA_W:', EXPERIENCIA_W); // Verifica el contenido del arreglo
    } catch (error) {
        console.error('Error al recibir datos:', error);
    }
}

fetchData();
