import React, { useRef, useState, useEffect } from "react";
import './Experiencia.css';
import ExperienceCard from "./ExperieneCard/ExperienceCard";
import Slider from "react-slick";

const Experiencia = ({ language }) => {
    const [experienciaW, setExperienciaW] = useState([]);
    const [selectedMovie, setSelectedMovie] = useState(null); // Estado para la película seleccionada
    const [showtimes, setShowtimes] = useState([]); // Estado para los showtimes

    const sliderRef = useRef();

    useEffect(() => {
        async function fetchData() {
            try {
                const response = await fetch('http://127.0.0.1:8000/movies');
                if (!response.ok) {
                    throw new Error('Network response was not ok ' + response.statusText);
                }
                const data = await response.json();
                console.log('Datos recibidos:', data);

                // Actualiza el estado con los datos
                const transformedData = data.map(item => ({
                    id: item.id, // Asegúrate de incluir el ID
                    title: item.title,
                    date: item.release_date,
                    responsabilidades: [item.description],
                    imagenes: [item.image_url]
                }));

                setExperienciaW(transformedData);
                console.log('EXPERIENCIA_W:', transformedData);
            } catch (error) {
                console.error('Error al recibir datos:', error);
            }
        }

        fetchData();
    }, []); // Ejecuta el efecto solo una vez, cuando el componente se monta

    // Función para manejar la selección de una película
    const handleSelectMovie = async (movie) => {
        setSelectedMovie(movie); // Actualiza el estado con la película seleccionada

        try {
            const response = await fetch(`http://127.0.0.1:8000/showtimes/${movie.id}`);
            if (!response.ok) {
                throw new Error('Network response was not ok ' + response.statusText);
            }
            const data = await response.json();
            setShowtimes(data); // Actualiza el estado con los showtimes de la película seleccionada
        } catch (error) {
            console.error('Error fetching showtimes:', error);
        }
    };

    const settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 2,
        slidesToScroll: 1,
        arrows: false,
        responsive: [
            {
                breakpoint: 769,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                },
            },
        ],
    };

    const derecha = () => {
        sliderRef.current.slickNext();
    };

    const izquierda = () => {
        sliderRef.current.slickPrev();
    };

    return (
        <section className="exp-contenedor">
            <h5>{language === 'es' ? 'Peliculas' : 'Movies'}</h5>

            <div className="exp-cont">
                <div className="arrow-right" onClick={derecha}>
                    <span className="material-symbols-outlined">chevron_right</span>
                </div>

                <div className="arrow-left" onClick={izquierda}>
                    <span className="material-symbols-outlined">chevron_left</span>
                </div>
                <Slider ref={sliderRef} {...settings}>
                    {experienciaW.length > 0 ? (
                        experienciaW.map((item) => (
                            <ExperienceCard key={item.id} detalles={item} onSelect={handleSelectMovie} />
                        ))
                    ) : (
                        <p>No hay datos disponibles</p>
                    )}
                </Slider>
            </div>

            {/* Renderiza la card con los detalles de la película seleccionada y sus showtimes */}
            {selectedMovie && (
                <div className="selected-movie-card">
                    <h2>{selectedMovie.title}</h2>
                    <p>{selectedMovie.responsabilidades[0]}</p>
                    <img src={selectedMovie.imagenes[0]} alt={selectedMovie.title} />

                    <h3>Showtimes</h3>
                    <ul>
                        {showtimes.length > 0 ? (
                            showtimes.map((showtime, index) => (
                                <li key={index}>{showtime.time} - Sala: {showtime.sala}</li>
                            ))
                        ) : (
                            <p>No hay showtimes disponibles</p>
                        )}
                    </ul>
                </div>
            )}
        </section>
    );
};

export default Experiencia;
