import React, { useRef, useState, useEffect } from "react";
import './Experiencia.css';
import ExperienceCard from "./ExperieneCard/ExperienceCard";
import Slider from "react-slick";

// Asegúrate de que esta función esté en el archivo adecuado y se esté importando correctamente
import { fetchData } from 'src/utils/data.js';


const Experiencia = ({ language }) => {
    const [experienciaW, setExperienciaW] = useState([]); // Estado inicial vacío
    const sliderRef = useRef();

    useEffect(() => {
        async function getData() {
            const data = await fetchData();
            setExperienciaW(data); // Actualiza el estado con los datos obtenidos
        }

        getData();
    }, []); // El array vacío asegura que se ejecute solo una vez al montar el componente

    const settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 2,
        slidesToScroll: 1,
        arrows: false,
        responsive: [
            {
                breakpoint: 769,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                },
            },
        ],
    };

    const derecha = () => {
        sliderRef.current.slickNext();
    };

    const izquierda = () => {
        sliderRef.current.slickPrev();
    };

    return (
        <section className="exp-contenedor">
            <h5>{language === 'es' ? 'Peliculas' : 'Movies'}</h5>

            <div className="exp-cont">
                <div className="arrow-right" onClick={derecha}>
                    <span className="material-symbols-outlined">chevron_right</span>
                </div>

                <div className="arrow-left" onClick={izquierda}>
                    <span className="material-symbols-outlined">chevron_left</span>
                </div>
                <Slider ref={sliderRef} {...settings}>
                    {experienciaW.length > 0 ? (
                        experienciaW.map((item) => (
                            <ExperienceCard key={item.title} detalles={item} />
                        ))
                    ) : (
                        <p>Cargando datos...</p> // Mensaje de carga mientras se obtienen los datos
                    )}
                </Slider>
            </div>
        </section>
    );
};

export default Experiencia;
