 export const EXPERIENCIA_W = [
    {
        title: "",
        date: "",
        responsabilidades: [],
        imagenes: []
    }
];

async function fetchData() {
    try {
        const response = await fetch('http://127.0.0.1:8000/movies'); // Reemplaza con tu endpoint de la API
        const data = await response.json();

        // Limpiamos el arreglo para evitar datos repetidos en múltiples llamadas
        EXPERIENCIA_W.length = 0;

        data.forEach(item => {
            EXPERIENCIA_W.push({
                title: item.title,
                date: item.release_date, // Aquí lo renombramos a 'date' como en tu arreglo
                responsabilidades: [item.description], // Agregamos description al arreglo de responsabilidades
                imagenes: [item.image_url] // Agregamos image_url al arreglo de imágenes
            });
        });
    } catch (error) {
        console.error('Error al recibir datos:', error);
    }
}

fetchData();
